CREATE DATABASE SD
COLLATE Latin1_General_100_BIN2_UTF8;

CREATE EXTERNAL DATA SOURCE catfact WITH (
    LOCATION = 'https://sdgr34ogc63f64kpoc.dfs.core.windows.net/apidata/facts/catfact_*.csv'
);
DROP EXTERNAL DATA SOURCE catfact;
CREATE MASTER KEY;

CREATE DATABASE SCOPED CREDENTIAL WorkspaceIdentity
WITH IDENTITY = 'Managed Identity';
GO
DROP EXTERNAL DATA SOURCE catfact;
CREATE EXTERNAL DATA SOURCE apidata WITH (
    LOCATION = 'https://sdgr34ogc63f64kpoc.dfs.core.windows.net/apidata/',
    CREDENTIAL = WorkspaceIdentity
);
drop external file format CsvWithHeader;
CREATE EXTERNAL FILE FORMAT CsvWithHeader
WITH (  
    FORMAT_TYPE = DELIMITEDTEXT,
    FORMAT_OPTIONS ( FIELD_TERMINATOR = ',', STRING_DELIMITER = '"', FIRST_ROW = 2   )
);
create schema SD;

drop external table SD.cat_fact;
create external table SD.cat_fact (
        [fact] varchar(max),
        [load_date] DATETIME
) with (
    data_source=apidata,
    location='facts/catfact_*.csv',
    file_format = CsvWithHeader,
    reject_value = 10000
);


select top 10 *
from openrowset(
        bulk 'facts/*.csv',
        data_source = 'apidata',
        format = 'csv',
        parser_version ='2.0',
        firstrow = 2
    ) as rows;

select * from SD.cat_fact;
